%% Create Money.Net Connection
    % Input username and password
    username = 'unc1@unc.edu';
    pwd = 'moneynet';
    
    c = moneynet(username,pwd); % create connection
    
%% Retrieve Money.Net last month
    
   
    f = {'Market Cap' ,'Average Volume' ,'EPS' ,'Price to Book Ratio'}; % data fields we want

 d = getdata(c,symbols,f); % retrieve data
    
    close(c); % close connection
    
 %Form a data table for ease of anaylsis    
    permno = d(:,1); cap = d(:,2); aveVol = d(:,3); eps = d(:,4); p2b = d(:,5); 
    Univ1 = [permno, cap,aveVol,eps,p2b] %Denote this as the first "universe" of assets. 
    
%%
%Let's look at the data
    summary(Univ1)
    Univ1(1:25,:)
    
%Find unique permnos
    upermno = unique(permno); 
    nu = size(upermno,1); %# of unique permno's (assets) 
    td = ['Number of assets=',num2str(nu)]; disp(td); clear td
    

%% Screen 1: Take only those assets in the top 10% of size
 
    upperquantile = .20; %user defined. Captures the top 10% for the screen
    
    Temp = sortrows(Univ1,'MarketCap');%Sort from smallest to largest according to size
    
    orderstat = round((1-upperquantile)*size(Temp,1),0); %Find the order statistic i.e. which observations to choose

    Temp2 = Temp(orderstat:end,:); %Pick only those assets above the orderstat i.e. meet the critertion
    
    Univ2 = innerjoin(Univ1,Temp2); %Permno is the unique matching key.  Keeps only those from Univ1 that meet the criterion
 
    nu = size(unique(Univ2(:,1)),1); 
    td = ['Size of Univ2 =',num2str(nu)]; disp(td); 
    
    clear Temp Temp2 upperquantile orderstat nu td %clean up
    

%% Screen 2: Take only those assets in the top 50% of volume AFTER conditioning
%upon size
    quantile = .50; %user defined.  Captures the top 5% for the screen
    
    Temp = sortrows(Univ2,'AverageVolume');%Sort from smallest to largest according to volume
    
    orderstat = round((quantile)*size(Temp,1),0); %Find the order statistic i.e. which observations to choose

    Temp2 = Temp(orderstat:end,:); %Pick only those assets above the orderstat i.e. meet the critertion
    
    Univ3 = innerjoin(Univ2,Temp2); %Permno is the unique matching key.  Keeps only those from Univ2 that meet the criterion
    
    nu = size(unique(Univ3(:,1)),1); 
    td = ['Size of Univ3 =',num2str(nu)]; disp(td); 
    
    clear Temp Temp2 quantile orderstat nu td %clean up

tickers= table2array(Univ3(:,1))
end
